mapper.update:
update
    SOLICITUD_REVISION
set
    SECUENCIA = #{secuencia,jdbcType=DECIMAL},
    FECHA_MODIFICACION = sysdate,
where  
    TITLE = #{title,jdbcType=VARCHAR} 
    AND COD_INTERESADO = #{codInteresado,jdbcType=DECIMAL} 
    and COD_RELACION = #{codRelacion,jdbcType=DECIMAL}

mapper.delete:
delete from
    SOLICITUD_REVISION
where
    TITLE = #{title,jdbcType=VARCHAR} 
    AND COD_INTERESADO = #{codInteresado,jdbcType=DECIMAL} 
    and COD_RELACION = #{codRelacion,jdbcType=DECIMAL}
