    public void doFilter(ServletRequest req, ServletResponse res, FilterChain chain) throws IOException, ServletException {
        HttpSession session;
        String msgAviso = "";
        Boolean irDireccion = false;

        if (((HttpServletRequest) req).getServletPath().substring(1).equals("entrada.jsf")) {
            chain.doFilter(req, res);
        } else {
            if (req instanceof HttpServletRequest) {
                HttpServletRequest httpSRequest = ((HttpServletRequest) req);
                session = httpSRequest.getSession();
                SesionRDU sesionWeb = (SesionRDU) session.getAttribute(SesionRDU.class.getName());
                if (sesionWeb != null) {
                    chain.doFilter(req, res);
                } else {
                    if (crearPrimerSesion(httpSRequest, session)) {

                        chain.doFilter(req, res);
                    } else {
                        String pgRedic = httpSRequest.getContextPath() + "/content/common/authentication/sinacceso.faces";
                        ((HttpServletResponse) res).sendError(1207, "SIN ACCESO AL  APLICATIVO RDU");
                        //String pgRedic=httpSRequest.getContextPath() +"/index.faces";
                        //((HttpServletResponse)res).sendRedirect(pgRedic);
                        //httpSRequest.getRequestDispatcher(pgRedic).forward(req, res);
                        return;
                        //chain.doFilter(req, res);
                    }
                }

            }
        }
    }

    private boolean crearPrimerSesion(HttpServletRequest httpSRequest, HttpSession session) throws IOException {
        String msgAviso = "";
        String encXmlPromovente = "";
        Boolean retResultado = false;
        XStream xstream = new XStream();
        Usuario nvoUsuario = new Usuario();

        try {
            log.info("LoginFilter.crearPrimerSesion: Leyendo parametro PORTAL_XMLPromovente");
            encXmlPromovente = httpSRequest.getParameter("PORTAL_XMLPromovente");
            area = httpSRequest.getParameter("PORTAL_Area");

//            String ambienteProduccion = bundleParametros.getString("action.ambienteProduccion");
            String ambienteProduccion = (String) JndiPropertiesUtils.getProperty("action.ambienteProduccion");

            //vamos mostrando los parámetros en unwhile
            Enumeration paramNames = httpSRequest.getParameterNames();
            while (paramNames.hasMoreElements()) {
                //cogemos el siguiente parámetro
                String paramName = (String) paramNames.nextElement();

                //Mostramos el nombre del parámetro 	
                log.info("LoginFilter.crearPrimerSesion: PARAMETROS ENCONTRADOS =" + paramName);
            }

            //0= desarrollo harcod parametro
            if (ambienteProduccion.equals("0")) {
                encXmlPromovente = valorParametroLogin();

            }

            if (encXmlPromovente == null) {
                msgAviso = "PORTAL_XMLPromovente generado por PASE se encuentra vacio, no se pudo generar la seguridad";
            } else {
                log.info("LoginFilter.crearPrimerSesion: valor del parametro PORTAL_XMLPromovente CODIFICADO:");
//                log.info(encXmlPromovente);

                PromoventeDto oPromovente = new PromoventeDto();
                //oPromovente = (PromoventeDto) xstream.fromXML(encXmlPromovente);
                //decodificar
                DataEncoding oDecodificacion = DataEncodingB64Impl.getInstance();
                String cadXmlPromovente = oDecodificacion.decodeData(encXmlPromovente);
                log.info("LoginFilter.crearPrimerSesion: valor del parametro PORTAL_XMLPromovente deCODIFICADO:");
//                log.info(cadXmlPromovente);
                InputStream xmlPromovente = new ByteArrayInputStream(cadXmlPromovente.getBytes("UTF-8"));
                PromoventeParserSAXImpl parserSAXImpl = new PromoventeParserSAXImpl(xmlPromovente);
                oPromovente = parserSAXImpl.getPromovente();

                /*
                 *Creacion de atibutos del promovente con datos
                 * del glassfish para desarrollo
                 *
                 */

                if (ambienteProduccion.equals("0")) {
                    crearUsuarioDesarrollo(oPromovente);
                   area = (String) JndiPropertiesUtils.getProperty("idArea");
                   // area = "20";
                    session.setAttribute("area", area);
                }


                if (oPromovente != null) {
                    log.info("dto promovente:" + oPromovente.getNombre());
                    nvoUsuario = creaUsuarioDePromovente(oPromovente);
                    if (nvoUsuario != null) {

                        SesionRDU sesionWeb = new SesionRDU();
                        sesionWeb.setUsuario(nvoUsuario);
                        sesionWeb.setPromovente(oPromovente);
                        session.setAttribute(SesionRDU.class.getName(), sesionWeb);
                        session.setAttribute("area", area);

                        /*
                        Atributo para mostrar la configuracion correspondiente
                         * utilizada en el menu, tablero de control y documentos en
                         * preparacion.
                         * */
                        session.setAttribute("ambienteProduccion", ambienteProduccion);

                        //log.info(">>>> SE CREO LA SESION");
                        msgAviso = "sesion creada con usuario y promovente: " + nvoUsuario.getUsuarioSeguridad().getUsuarioStr();
                        retResultado = true;

                    } else {
                        msgAviso = "no se creo el usuario y no se implemento la seguridad";
                    }

                } else {
                    msgAviso = "no se creo el prommovente y no se implemento la seguridad";
                }

            }
            log.info(msgAviso);
            return retResultado;
        } catch (Exception e) {
            System.out.println("Ocurrio un error en el metodo LoginFilter.crearPrimerSesion ->" + e);
            return retResultado;
        }

    }

    public void crearUsuarioDesarrollo(PromoventeDto promovente) {
        String ambienteProduccion;
        try {
            ambienteProduccion = (String) JndiPropertiesUtils.getProperty("action.ambienteProduccion");
            if (ambienteProduccion.equals("0")) {
                promovente.setLogin((String) JndiPropertiesUtils.getProperty("nombrePromovente"));
                promovente.setNombre((String) JndiPropertiesUtils.getProperty("nombrePromovente"));
                promovente.setApaterno((String) JndiPropertiesUtils.getProperty("aPaternoPromovente"));
                promovente.setAmaterno((String) JndiPropertiesUtils.getProperty("aMaternoPromovente"));
                promovente.setId_perfil(new Integer((String) JndiPropertiesUtils.getProperty("idPerfilPromovente")));
                promovente.setId_promovente(new Integer((String) JndiPropertiesUtils.getProperty("idPromovente")));
                promovente.setTipo_persona(1);
            }
        } catch (NamingException ex) {
            log.info("Propiedad action.ambienteProduccion no declarada en el glassfish");
        }
    }

